data.query:
query { repository(owner: "designsystemau", name: "blog-site") {
    name
    codeOfConduct {
      body
    }
    issues(first: 20) {
      totalCount
      nodes {
        id
        url
        number
        lastEditedAt
        createdAt
        title
        bodyHTML
        participants(first: 20) {
          totalCount
          nodes {
            id
            url
            name
            avatarUrl
          }
        }
        comments(first: 1) {
          totalCount
          nodes {
            id
            url
            lastEditedAt
            createdAt
            bodyHTML
            author {
              url
              avatarUrl
              login
            }
          }
        }
      }
    }
    pullRequests(first: 20) {
      totalCount
      nodes {
        id
        permalink
        title
        participants(first: 20) {
          totalCount
          nodes {
            id
            url
            name
            avatarUrl
          }
        }
        comments(first: 1) {
          totalCount
          nodes {
            id
            url
            lastEditedAt
            createdAt
            bodyHTML
            author {
              url
              avatarUrl
              login
            }
          }
        }
      }
    }
  }
}
